css:
.datatip-element {
  overflow: auto;
  color: #abb2bf;
  white-space: nowrap;
  pointer-events: all;
  user-select: text;
  font-family: system-ui;
  font-size: var(--editor-font-size);
  max-height: 500px;
  max-width: 700px;
}
.datatip-element pre {
  font-family: var(--editor-font-family);
  font-size: var(--editor-font-size);
  margin: 2px;
  margin-bottom: 8px;
  border-radius: 0;
}
.datatip-element p {
  margin-left: 8px;
  margin-right: 8px;
}
.datatip-element p:last-child {
  margin-bottom: 8px;
}
.datatip-element.select-list.popover-list {
  width: auto;
}
.datatip-overlay {
  border-bottom: 4px solid #31363f;
  border-radius: 3px;
  transition: border-bottom-color 0.15s ease;
}
.datatip-overlay:hover {
  border-bottom-color: #353b45;
}
/** Applied to ReactView (Components) */
.datatip-component-container {
  display: flex;
  position: relative;
  color: #abb2bf;
  white-space: normal;
}
.datatip-component-container :first-child {
  margin-top: 0;
}
.datatip-component-container :last-child {
  margin-bottom: 0;
}
.datatip-component-container:not(:last-of-type) {
  border-bottom: 1px solid rgba(82, 139, 255, 0.1);
}
.datatip-component {
  flex-grow: 1;
}
.datatip-component .list-tree {
  cursor: inherit;
}
/** Applied to MarkdownView */
.datatip-markdown > div:not(:last-child) {
  border-bottom: 1px solid rgba(82, 139, 255, 0.1);
}
.datatip-markdown-container {
  color: #abb2bf;
  white-space: normal;
}
.datatip-markdown-container :first-child {
  margin-top: 0;
}
.datatip-markdown-container :last-child {
  margin-bottom: 0;
}
.datatip-markdown-container p:first-child {
  margin-top: 8px;
}
.datatip-markdown-container p:last-child {
  margin-bottom: 8px;
}
/** Applied to SnippetView */
.datatip-snippet-container {
  overflow: auto;
}
.datatip-snippet {
  flex-grow: 1;
}
.datatip-snippet .cursors {
  display: none;
}
atom-text-editor .datatip-highlight-region > .region {
  background: rgba(82, 139, 255, 0.1);
}
