#linter-panel {
  overflow: auto;
}
#linter-tooltip {
  margin-top: 3px;
  max-width: 64em;
  visibility: hidden;
  padding: 5px 5px 2px 5px;
  font-family: "BlinkMacSystemFont", "System Font", "Helvetica Neue", "Roboto", "Cantarell", "Ubuntu", "Lucida Grande", "Segoe UI", "Noto Sans", sans-serif;
  user-select: text;
  pointer-events: all;
}
#linter-tooltip .linter-message {
  padding: 10px;
  color: #c4bdad;
  background: #2E3440;
}
#linter-tooltip .linter-message .linter-excerpt {
  padding: 5px;
  display: flex;
  align-items: center;
}
#linter-tooltip .linter-message .linter-excerpt:not(:last-child) {
  border-bottom: 1px solid rgba(255, 255, 255, 0.05);
}
#linter-tooltip .linter-message .linter-excerpt.error {
  border-left: 4px solid #BF616A;
}
#linter-tooltip .linter-message .linter-excerpt.error:last-child::before {
  border-color: transparent transparent #BF616A #BF616A;
}
#linter-tooltip .linter-message .linter-excerpt.warning {
  border-left: 4px solid #D08770;
}
#linter-tooltip .linter-message .linter-excerpt.warning:last-child::before {
  border-color: transparent transparent #D08770 #D08770;
}
#linter-tooltip .linter-message .linter-excerpt.info {
  border-left: 4px solid #88C0D0;
}
#linter-tooltip .linter-message .linter-excerpt.info:last-child::before {
  border-color: transparent transparent #88C0D0 #88C0D0;
}
#linter-tooltip .linter-message .linter-excerpt .linter-text {
  margin-left: 0.5em;
}
#linter-tooltip .linter-message .linter-excerpt .provider-name {
  display: inline;
  font-weight: bolder;
}
#linter-tooltip .linter-message .linter-excerpt .linter-buttons-right {
  margin-left: 10px;
}
#linter-tooltip .linter-message .linter-excerpt .linter-buttons-right a {
  color: #D8DEE9;
}
#linter-tooltip .linter-message .linter-excerpt .fix-btn {
  margin-left: 5px;
}
#linter-tooltip .linter-message a {
  font-weight: bold;
}
#linter-tooltip .linter-message p {
  margin-bottom: 0;
}
#linter-tooltip .linter-message .linter-line {
  display: block;
}
#linter-tooltip .linter-message .badge {
  color: #ECEFF4;
}
.linter-message {
  display: block;
  padding: 2px 5px;
}
.icon.linter-icon::before {
  font-size: 14px;
}
atom-text-editor.editor .linter-row {
  /* Take up the full allowed width */
  left: 0;
  right: 0;
  /* Align the linter dot in the middle */
  display: flex;
  align-items: center;
  justify-content: center;
}
.linter-gutter.linter-gutter-info {
  color: #88C0D0;
  z-index: -1;
}
.linter-gutter.linter-gutter-warning {
  color: #D08770;
  z-index: 0;
}
.linter-gutter.linter-gutter-error {
  color: #BF616A;
  z-index: 1;
}
atom-text-editor.editor .linter-highlight.linter-info,
.linter-highlight.linter-info {
  background-image: linear-gradient(45deg, transparent 65%, #88C0D0 80%, transparent 90%), linear-gradient(135deg, transparent 5%, #88C0D0 15%, transparent 25%), linear-gradient(135deg, transparent 45%, #88C0D0 55%, transparent 65%), linear-gradient(45deg, transparent 25%, #88C0D0 35%, transparent 50%);
  background-size: 8px 2px;
  background-repeat: repeat-x;
  background-position: left bottom;
}
atom-text-editor.editor .linter-highlight.linter-warning,
.linter-highlight.linter-warning {
  background-image: linear-gradient(45deg, transparent 65%, #D08770 80%, transparent 90%), linear-gradient(135deg, transparent 5%, #D08770 15%, transparent 25%), linear-gradient(135deg, transparent 45%, #D08770 55%, transparent 65%), linear-gradient(45deg, transparent 25%, #D08770 35%, transparent 50%);
  background-size: 8px 2px;
  background-repeat: repeat-x;
  background-position: left bottom;
}
atom-text-editor.editor .linter-highlight.linter-error,
.linter-highlight.linter-error {
  background-image: linear-gradient(45deg, transparent 65%, #BF616A 80%, transparent 90%), linear-gradient(135deg, transparent 5%, #BF616A 15%, transparent 25%), linear-gradient(135deg, transparent 45%, #BF616A 55%, transparent 65%), linear-gradient(45deg, transparent 25%, #BF616A 35%, transparent 50%);
  background-size: 8px 2px;
  background-repeat: repeat-x;
  background-position: left bottom;
}
.tree-view span.name {
  position: relative;
}
.tree-view .linter-info {
  top: 0;
  left: 1.8rem;
  width: calc(100% - 1.8rem);
  height: 100%;
  position: absolute;
  background-image: linear-gradient(45deg, transparent 65%, #88C0D0 80%, transparent 90%), linear-gradient(135deg, transparent 5%, #88C0D0 15%, transparent 25%), linear-gradient(135deg, transparent 45%, #88C0D0 55%, transparent 65%), linear-gradient(45deg, transparent 25%, #88C0D0 35%, transparent 50%);
  background-size: 8px 2px;
  background-repeat: repeat-x;
  background-position: left bottom;
}
.tree-view .linter-warning {
  top: 0;
  left: 1.8rem;
  width: calc(100% - 1.8rem);
  height: 100%;
  position: absolute;
  background-image: linear-gradient(45deg, transparent 65%, #D08770 80%, transparent 90%), linear-gradient(135deg, transparent 5%, #D08770 15%, transparent 25%), linear-gradient(135deg, transparent 45%, #D08770 55%, transparent 65%), linear-gradient(45deg, transparent 25%, #D08770 35%, transparent 50%);
  background-size: 8px 2px;
  background-repeat: repeat-x;
  background-position: left bottom;
}
.tree-view .linter-error {
  top: 0;
  left: 1.8rem;
  width: calc(100% - 1.8rem);
  height: 100%;
  position: absolute;
  background-image: linear-gradient(45deg, transparent 65%, #BF616A 80%, transparent 90%), linear-gradient(135deg, transparent 5%, #BF616A 15%, transparent 25%), linear-gradient(135deg, transparent 45%, #BF616A 55%, transparent 65%), linear-gradient(45deg, transparent 25%, #BF616A 35%, transparent 50%);
  background-size: 8px 2px;
  background-repeat: repeat-x;
  background-position: left bottom;
}
.linter-gutter.icon::before {
  width: 100%;
  margin-right: 0px;
  font-size: 1em;
}
.linter-status-count a {
  padding: 2px;
}
.linter-status-count a::before {
  font-size: 12px;
}
.linter-status-count.hide-config,
.linter-status-count.hide-pane {
  display: none;
}
.linter-cursor-line {
  width: 100%;
  padding-left: 0;
}
linter-decoration {
  pointer-events: none;
}
