.signature-element {
  overflow: auto;
  background: #2E3440;
  box-shadow: 0px 1px 4px 0px #000000;
  color: #abb2bf;
  font-size: 1rem;
  margin-top: -2px;
  position: relative;
  white-space: nowrap;
  user-select: text;
  pointer-events: all;
  display: block;
  visibility: hidden;
  padding: 5px 5px 2px 5px;
  font-family: "BlinkMacSystemFont", "System Font", "Helvetica Neue", "Roboto", "Cantarell", "Ubuntu", "Lucida Grande", "Segoe UI", "Noto Sans", sans-serif;
  font-size: var(--editor-font-size);
  max-height: 480px;
  max-width: 600px;
}
.signature-element pre {
  font-family: var(--editor-font-family);
  font-size: var(--editor-font-size);
  margin: 2px;
  margin-bottom: 8px;
  border-radius: 0;
}
.signature-element p {
  margin-left: 8px;
  margin-right: 8px;
}
.signature-element p:last-child {
  margin-bottom: 8px;
}
.signature-overlay {
  border-bottom: 5px solid #434C5E;
  border-radius: 3px;
  transition: border-bottom-color 0.15s ease;
}
.signature-overlay:hover {
  border-bottom-color: #58647c;
}
.signature-content {
  flex-grow: 1;
}
.signature-content .list-tree {
  cursor: inherit;
}
/** Applied to SnippetView */
.signature-snippet-container {
  overflow: auto;
  color: #abb2bf;
}
.signature-snippet-container :first-child {
  margin-top: 0;
}
.signature-snippet-container :last-child {
  margin-bottom: 0;
}
.signature-snippet-container:not(:last-of-type) {
  border-bottom: 1px solid rgba(82, 139, 255, 0.1);
}
.signature-snippet {
  flex-grow: 1;
}
.signature-snippet .cursors {
  display: none;
}
/** Applied to MarkdownView */
.signature-markdown > div:not(:last-child) {
  border-bottom: 1px solid rgba(82, 139, 255, 0.1);
}
.signature-markdown-container {
  color: #abb2bf;
  white-space: normal;
}
.signature-markdown-container :first-child {
  margin-top: 0;
}
.signature-markdown-container :last-child {
  margin-bottom: 0;
}
.signature-markdown-container p:first-child {
  margin-top: 8px;
}
.signature-markdown-container p:last-child {
  margin-bottom: 8px;
}
