@font-face {
  font-family: "symbol-icons";
  src: url("atom://atom-ide-outline/styles/symbol-icons.woff") format("woff");
  font-weight: normal;
  font-style: normal;
}
.atom-ide-outline {
  display: flex;
  flex-direction: column;
}
.atom-ide-outline .outline-content {
  contain: layout paint style;
  flex-grow: 1;
  flex-shrink: 1;
  overflow: auto;
  --editor-tab-length: 4;
  --level-indent-size: 16px;
  --indent-level: 0;
}
.atom-ide-outline .outline-content > ul {
  width: fit-content;
}
.atom-ide-outline .outline-content ul {
  contain: layout paint style;
  list-style-type: none;
  padding: 0;
  font-size: var(--editor-font-size);
  font-family: var(--editor-font-family);
  line-height: var(--editor-line-height);
}
.atom-ide-outline .outline-content ul li > span {
  contain: layout paint style;
  display: inline-block;
  width: 100%;
  padding: 2px 10px;
  white-space: nowrap;
  padding-left: calc(var(--editor-tab-length) * var(--level-indent-size) * var(--indent-level) + 10px + 8px);
}
.atom-ide-outline .outline-content ul li > span:hover {
  contain: layout paint style;
  cursor: pointer;
  background: #434C5E;
}
.atom-ide-outline .outline-content ul li[cursorOn] {
  contain: layout paint style;
  background: #434C5E;
}
.atom-ide-outline .outline-content ul li[cursorOn].after-border {
  border-bottom: 1px solid #71844c;
}
.atom-ide-outline .outline-content .outline-fold-btn {
  contain: layout paint style;
  display: inline-block;
  position: relative;
  width: 8px;
  margin: 0 0 0 -8px;
  padding: 0;
  opacity: 1;
  border: none;
  background-color: inherit;
  text-align: center;
  text-decoration: none;
  vertical-align: middle;
  font-family: "Octicons Regular";
  font-size: 12px;
  font-style: normal;
  font-weight: normal;
  -webkit-font-smoothing: antialiased;
}
.atom-ide-outline .outline-content .outline-fold-btn.collapsed::before {
  contain: layout paint style;
  content: "\f078";
}
.atom-ide-outline .outline-content .outline-fold-btn.expanded::before {
  contain: layout paint style;
  content: "\f0a3";
}
.atom-ide-outline .outline-content .large-file-mode {
  contain: layout paint style;
  list-style-type: none;
  padding: 2px 10px;
  font-size: var(--editor-font-size);
  font-family: var(--editor-font-family);
  line-height: var(--editor-line-height);
  color: #71844c;
}
.atom-ide-outline .outline-content .status {
  contain: layout paint style;
  height: 100%;
  display: flex;
  flex-direction: column;
  justify-content: center;
  text-align: center;
  padding: 0 10px;
}
.atom-ide-outline .outline-content .status h1 {
  font-size: 1.5rem;
}
.atom-ide-outline .outline-toolbar {
  flex-grow: 0;
  flex-shrink: 0;
  display: flex;
  flex-direction: row;
  padding: 8px 15px;
}
.atom-ide-outline .outline-searchbar {
  padding: 8px 15px;
}
